import React, { useState, useEffect } from 'react';
import { PageContainer } from '@ant-design/pro-layout';
import { Button, Card, Image, message, Typography } from 'antd';
import { history } from 'umi';
import { FolderAddOutlined } from '@ant-design/icons';
import { listAlbums, listSharedAlbums } from '@/services/api';
import { isArray } from 'lodash';
import { convertBlobToUrl } from '@/utils/utils';
import { getToken } from '@/utils/authority';

const { Title, Text } = Typography;

export default (): React.ReactNode => {
    const [albums, setAlbums] = useState<any[]>([]);
    const [sharedAlbums, setSharedAlbums] = useState<any[]>([]);

    const [loadingAlbums, setLoadingAlbums] = useState<boolean>(true);
    const [loadingShared, setLoadingShared] = useState<boolean>(true);

    useEffect(() => {
        if (getToken()) {
            refreshAlbums();
            refreshSharedAlbums();
        }
    }, []);

    const refreshAlbums = async () => {
        try {
            const response = await listAlbums();
            if (isArray(response)) {
                setAlbums(response)
            }
        } catch (error) {
            message.error("Unable to refresh the albums!");
        }
        setLoadingAlbums(false);
    }

    const refreshSharedAlbums = async () => {
        try {
            const response = await listSharedAlbums();
            if (isArray(response)) {
                setSharedAlbums(response)
            }
        } catch (error) {
            message.error("Unable to refresh the shared albums!");
        }
        setLoadingShared(false);
    }

    const renderNoAlbums: JSX.Element = <>NO ALBUMS</>

    const handleAddClicked = () => {
        history.push('/album/add')
    }

    const handleViewClicked = (id: string) => {
        history.push(`/album/view?id=${id}`);
    }

    const renderAlbum = (album: any): JSX.Element => {
        let fileUrl = 'error';
        if (album?.previewFilename && album?.previewData) {
            fileUrl = convertBlobToUrl(album.previewData, album.previewFilename)
        }
        return <Card.Grid
            key={album.id}
            hoverable
            style={{ width: 250, height: 250 }}
        >
            <div onClick={() => handleViewClicked(album?.id)} style={{ textAlign: 'center' }}>
                <Title level={5}>{album.name}</Title>
                <Image
                    width={150}
                    height={150}
                    src={fileUrl}
                    fallback="data:image/png;base64,"
                />
                <br />
                <Text type='secondary' style={{ fontSize: '0.8em' }}>{album.description}</Text>
            </div>
        </Card.Grid>
    }

    const renderActions = (): JSX.Element => <>
        <Button type='primary' icon={<FolderAddOutlined />} onClick={handleAddClicked} >Add Album</Button>
    </>

    return (
        <PageContainer
            title={'Albums'}
            extra={renderActions()}
        >
            <Card title='My Albums' loading={loadingAlbums}>
                {albums.length > 0 && albums.map(album => renderAlbum(album))}
                {albums.length === 0 && renderNoAlbums}
            </Card>
            <br />
            <Card title='Albums Shared With Me' loading={loadingShared}>
                {sharedAlbums.length > 0 && sharedAlbums.map(album => renderAlbum(album))}
                {sharedAlbums.length === 0 && renderNoAlbums}
            </Card>
        </PageContainer>
    );
};
