import React, { useEffect, useState } from 'react';
import { PageContainer } from '@ant-design/pro-layout';
import { Button, Card, Empty, Image, message } from 'antd';
import { history } from 'umi';
import { VideoCameraAddOutlined } from '@ant-design/icons';
import { listPhotos } from '@/services/api';
import { isArray } from 'lodash';

export default (): React.ReactNode => {
    const [photos, setPhotos] = useState<any[]>([]);

    useEffect(() => {
        refreshPhotos();
    }, []);

    const refreshPhotos = async () => {
        try {
            const response = await listPhotos();
            if (isArray(response)) {
                setPhotos(response)
            }
        } catch (error) {
            message.error("Unable to refresh the photos!");
        }
    }

    const renderNoPhotos = (): JSX.Element => <>NO PHOTOS</>

    const handleAddClicked = () => {
        history.push('/photo/add');
    }

    const handleEditClicked = (id: string) => {
        history.push(`/photo/edit?id=${id}`);
    }

    const renderPhoto = (photo: any): JSX.Element => {
        return <Card
            key={photo.id}
            hoverable
            style={{ width: 200 }}
            cover={<Image
                width={200}
                height={200}
                src="error"
                fallback="data:image/png;base64,"
            />}
            onClick={() => handleEditClicked(photo?.id)}
        >
        </Card>
    }

    const renderActions = (): JSX.Element => <>
        <Button type='primary' icon={<VideoCameraAddOutlined />} onClick={handleAddClicked} >Add Photo</Button>
    </>

    return (
        <PageContainer
            title={'Photos'}
            extra={renderActions()}
        >
            <Card >
                {photos.length > 0 && photos.map(photo => renderPhoto(photo))}
                {photos.length === 0 && renderNoPhotos()}
            </Card>
        </PageContainer>
    );
};
