import React, { useState, useEffect } from 'react';
import { PageContainer } from '@ant-design/pro-layout';
import { Button, Card, message, Tag, Typography, Image } from 'antd';
import { useHistory, useModel } from 'umi';
import styles from './index.less';
import { getPhoto } from '@/services/api';
import { ArrowLeftOutlined, FolderOutlined, FullscreenExitOutlined, ShareAltOutlined } from '@ant-design/icons';
import SharePhotoModal from '../Share';
import Photo from '..';
import { convertBlobToUrl, PresetColorType } from '@/utils/utils';

const { Title, Text } = Typography;


export default (props: any): React.ReactNode => {
    const { initialState, setInitialState } = useModel('@@initialState');
    const history = useHistory();

    const [shareModalState, setShareModalState] = useState<any>({ visible: false });
    const [loading, setLoading] = useState<boolean>(true);
    const [photo, setPhoto] = useState<any>(undefined);

    const [fileUrl, setFileUrl] = useState<any>(undefined);

    useEffect(() => {
        const params = new URLSearchParams(props.location.search);
        const id = params.get('id');

        if (id) {
            loadPhoto(id);
        }
        else {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        if (photo?.filename && photo?.data) {
            let url = convertBlobToUrl(photo.data, photo.filename)
            console.log(url)
            setFileUrl(url);
        }
    }, [photo]);

    const loadPhoto = async (id: string) => {
        try {
            const response = await getPhoto(id);
            if (response) setPhoto(response);
        } catch (error) {
            message.error("Unable to load the photo!");
        }
        setLoading(false);
    }

    const handleEditClicked = () => {
        history.push(`/photo/edit?id=${photo.id}`)
    }

    const handleShareClicked = () => {
        setShareModalState({ visible: true })
    }


    const renderActions = (): JSX.Element => <>
        {initialState?.currentUser?.id && photo?.createdByUserId === initialState?.currentUser?.id && <Button type='primary' icon={<FolderOutlined />} onClick={handleEditClicked} >Edit Photo</Button>}
        <Button type='default' icon={<ArrowLeftOutlined />} onClick={() => history.push(`/photos`)}>Back</Button>
    </>

    const renderCardActions = (): JSX.Element => <>
        {initialState?.currentUser?.id && photo?.createdByUserId === initialState?.currentUser?.id && <Button type='primary' icon={<ShareAltOutlined />} onClick={handleShareClicked} >Share Photo</Button>}
    </>

    return (
        <PageContainer
            extra={renderActions()}
        >
            <Card
                extra={renderCardActions()}
                loading={loading}
            >
                <div style={{ textAlign: 'center' }}>
                    <Image
                        style={{ maxWidth: 800, minWidth: 400 }}
                        src={fileUrl || "error"}
                        fallback="data:image/png;base64,"
                    />
                </div>
                <br />
                <div style={{ fontSize: '0.8em', textAlign: 'left' }}>
                    <span style={{ fontWeight: 'bold' }}>File:</span> {photo?.filename || 'N/A'}
                    <br />
                    <span style={{ fontWeight: 'bold' }}>Geolocation:</span> {photo?.metadata?.geolocation || 'N/A'}
                    <br />
                    <span style={{ fontWeight: 'bold' }}>Tags: </span> {photo?.metadata?.tags?.join(", ") || 'N/A'}
                </div>
            </Card>
            {shareModalState.visible && <SharePhotoModal photoId={photo?.id} name={photo?.filename} onCancel={() => { setShareModalState({ visible: false }) }} />}
        </PageContainer >
    );
};
